<?phpclass Producto_model extends CI_Model {    function __construct()    {        // Call the Model constructor        parent::__construct();    }        function get_product($UNSPSC)    {		$this->db->from('tbproducto');		$this->db->join('tbindicador','tbindicador.idProducto = tbproducto.idProducto');		$this->db->where('tbproducto.codUNSPSC',$UNSPSC);        $query = $this->db->get();				if ($query->num_rows() > 0)		{		   return $query->row_array(); 		}		else		{			return FALSE;		}    }    function insert_entry()    {        $this->title   = $_POST['title']; // please read the below note        $this->content = $_POST['content'];        $this->date    = time();        $this->db->insert('entries', $this);    }    function update_entry()    {        $this->title   = $_POST['title'];        $this->content = $_POST['content'];        $this->date    = time();        $this->db->update('entries', $this, array('id' => $_POST['id']));    }}?>